package LoopingStatements;

public class E {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num=1;
while(num<=10) {
	if(num%2==0) {
		System.out.println(num+"is even number");
	}
	else if(num%2==1)
	{
		System.out.println(num+"is odd number");
	}
	num++;
}
	}

}
