package basics;

public class PostDec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		int b=a++;
		int c=--b;
		System.out.println(c++);
		System.out.println(++b);
		System.out.println(a--);
		int d=--c;
		System.out.println(d+1);
		System.out.println(d--);
		System.out.println(d++);
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);

	}

}
